INSERT [dbo].[ShoppingItemsProfile] ([ID], [Name], [ImgUri], [Price], [Description]) VALUES (1, N'Macbook Air 13" M1 Vesmírne sivý', N'1.jpg', CAST(999.00 AS Decimal(10, 2)), N'Najtenší a najlahší notebook od spolocnosti Apple je teraz extrémne výkonný vdaka 5nm cipu Apple M1. Ten ponúka bleskovo rýchle 8-jadrové CPU, ktoré si poradia s každým projektom. A nechýba ani až 8-jadrový grafický cip, ktorého sila rozhýbe aj nárocné aplikácie a hry. A nesmieme zabudnút na 16-jadrový Neural Engine zrýchlujúci úlohy strojového ucenia. Toto je nový MacBook Air.')
